sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.statusCode == "200") {
    var obj = JSON.parse(inputs.responseBody);
    outputs.project = obj;
    gs.info(JSON.stringify(obj, null, 4));
  } else {
    gs.info('Create Todoist Project: Cannot parse response body');
  }
})(inputs, outputs);

